import React from 'react';
import {  
  Link,
  withRouter,
  Route,
  Redirect,
} from 'react-router-dom'
//import FormGroup from './form-group'


class Login extends React.Component {

  state = {
    email: '',
    senha: '',
    mensageErro: null,
    logar: false,
  }

  handlelogin = (user) => {
    saveUser(user)
      .then(() => this.setState(() => ({
        toDashboard: true
      })))
  }





/*  
 entrar = () => {
    axios
      .post('http://localhost:8080/api/usuarios/autenticar', {
        email: this.state.email,
        senha: this.state.senha
      }).then(response => {
        localStorage.setItem('_usuario_logado', JSON.stringify(response.data))
        this.props.history.push('/enquete')
      }).catch(erro => {
        this.setState({ mensageErro: erro.response.data })
      })

  }
  */
    // prepareLogin = () => {
    //   this.props.history.push('/enquete')
    // }

    //arrumar mensagem erro



  render() {

    if (this.state.logar === true) {
      return <Redirect to='/enquete' />
    }

    return (
      <div>
        <head>
          <meta charSet="UTF-8"></meta>
          <meta name="viewport" content="width=device-width, initial-scale=1.0"></meta>
          <title>Fala UFS</title>
        </head>
        <body>
          <header>
            <div className="header">
              <div>
                <span className="logo_img"></span>
                <span className="login_text">FAZER LOGIN COM SIGAA</span>
              </div>

            </div>
          </header>
          <div>
            <span className="smartufs"></span>
          </div>
          <div>
            <span className="dau"></span>
          </div>
          <div>
            <span className="dcomp"></span>
          </div>

          <div className="form-group">

            <input type="email"
              value={this.state.email}
              onChange={e => this.setState({ email: e.target.value })}
              className="form-control1" id="exampleInputEmail1"
              placeholder="UsuÃ¡rio">
            </input>

            <div className="form-group">
              <input type="password"
                value={this.state.senhal}
                onChange={e => this.setState({ senha: e.target.value })}
                className="form-control2"
                id="exampleInputPassword1"
                placeholder="Senha">
              </input>
            </div>

          </div>

          <button  onClick={ this.handlelogin()}  className="entrar" type="button">ENTRAR</button>

        </body>
      </div>
    );
  }
}
 //    {/* <button  onClick={ this.props.history.push('/enquete')}  className="entrar" type="button">ENTRAR</button> */}
export default withRouter(Login);
